/* MAIN */
const sfme = (a, t, n) => {
    let x = a % n;
    if (t <= BigInt(Number.MAX_SAFE_INTEGER)) {
        for (let i = Number(t); i > 0; i--) {
            x = (x * x) % n;
        }
    }
    else {
        for (let i = t; i > 0n; i--) {
            x = (x * x) % n;
        }
    }
    return x % n;
};
/* EXPORT */
export { sfme };
